import React , {Component} from 'react'

import {NavLink} from 'react-router-dom'

export default class SignUp extends Component {

    constructor(){
        super()
        this.state = {
            username_errors: "",
            password_errors: ""
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSignUP = () => {
        let obj = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username: this.state.username,
                password: this.state.password
            })
        }
        fetch("http://localhost:3000/user_signup",obj)
        .then(res => res.json())
        .then(data => data.errors ? this.setState({password_errors: data.errors.password, username_errors: data.errors.username}) : this.setState({password_errors: "", username_errors: ""}))
    }

    render(){
        return (
            <div className="login_container shadow-lg p-3 mb-5 bg-white rounded">
                <h1>Sign Up</h1>
                {Array.isArray(this.state.username_errors) ? this.state.username_errors.map(error => <li>{`Username ${error}`}</li>) : ""}
                {Array.isArray(this.state.password_errors) ? this.state.password_errors.map(error => <li>{`Password ${error}`}</li>) : ""}
                <div class="form-group login_content">
                    <h3 >Username</h3>
                    <input type="text" className={
                        this.state.username_errors[0] ? "form-control is-invalid"
                        : Array.isArray(this.state.username_errors) ? "form-control is-valid"
                        : "form-control"} name="username" onChange = {(e) => this.handleChange(e)}/>
                </div>
                <br/>
                <div class="form-group login_content">
                    <h3 >Password</h3>
                    <input type="password" className={
                        this.state.password_errors[0] ? "form-control is-invalid"
                        : Array.isArray(this.state.password_errors) ? "form-control is-valid"
                        : "form-control"} name="password" onChange = {(e) => this.handleChange(e)}/>
                </div>
                <br/>
                <div class="form-group login_content">
                    <h3 >Password Confirmation</h3>
                    <input type="password" className={
                        this.state.password_errors[0] ? "form-control is-invalid"
                        : Array.isArray(this.state.password_errors) ? "form-control is-valid"
                        : "form-control"} name="password_confirmation" onChange = {(e) => this.handleChange(e)}/>
                </div>
                <br/>
                <button className="btn btn-outline-success" onClick={() => this.handleSignUP()}>Sign Up</button>
                <br/>
                <button className="btn btn-outline-success">
                    <NavLink to ='/login'>Login</NavLink>
                </button>
            </div>
        )
    }
}
