import React from 'react'
import { Grid, Card, Image, Reveal } from 'semantic-ui-react'

const ChampionCard = (props) => {
  return(
    // <Grid.Column>
      <Card
        color='teal'
        >
        <Reveal.Content>
          <Image
            size='medium'
            // floated='left'
            src={props.champion.icon_image} />
        </Reveal.Content>
        <Reveal.Content hidden>
          sup
        </Reveal.Content>
        <Card.Content
        style={{color: 'black', fontWeight: 'bold'}}
        >
          {`${props.champion.name}, ${props.champion.title}`}
        </Card.Content>
      </Card>
    // </Grid.Column>
  )
}

export default ChampionCard
