import React from 'react'
import ChampionCard from '../components/ChampionCard.js'
import { Card, Reveal, Image } from 'semantic-ui-react'

const ChampionsCardContainer = (props) => {
  return(
    <div className="champions_card_container">
      <div style={{width: '1200px', position: 'relative', left: '50vw', transform: 'translateX(-50%)', backgroundColor: 'black'}}>

        <Card.Group itemsPerRow={4}>
          {this.props.champions.length > 0
            ? this.props.champions.map(champion =>
              <ChampionCard
                history={this.props.history}
                champion={champion}
                setChampionId={this.props.setChampionId} />)
            : this.generatePlaceholderRows(placeholders, 4)}
        </Card.Group>
      </div>
    </div>
  )
}

export default ChampionsCardContainer
