import React from 'react'
import { Grid, Card, Image, Reveal, Placeholder, Transition } from 'semantic-ui-react'

class ChampionCard extends React.Component {
  state={ showInfo: false }

  transition = () => {
    this.setState({
      showInfo: true
    })
  }

  render() {
    return(
      <Card style={{backgroundColor: 'black'}} color='teal' onMouseOver={this.transition}>

        <Reveal instant animated='fade'>

          <Reveal.Content visible style={{height: '100px', width: '100px', position: 'absolute'}}>
            <Card.Content>
              <Image size='medium' src={this.props.champion.icon_image} />
              {/* <Image src='https://image.freepik.com/free-photo/image-human-brain_99433-298.jpg' size='large' square /> */}
            </Card.Content>
          </Reveal.Content>

          <Reveal.Content hidden style={{position: 'absolute'}}>
            <Card.Content>
            <Transition visible={this.state.showInfo} animation='scale' duration={500} style={{position: 'absolute'}}>
                  <h2>hello</h2>
                </Transition>
              {/* <Placeholder>
                <Placeholder.Image size='large' square />
              </Placeholder> */}

            </Card.Content>
          </Reveal.Content>
        </Reveal>

        <Card.Content style={{fontWeight: 'bold'}}>
          {`${this.props.champion.name}, ${this.props.champion.title}`}
        </Card.Content>
      </Card>
    )
  }

}

export default ChampionCard
