import React , {Component} from "react" 
import ProfileContainer from "./ProfileContainer"
import SummonerChampionsContainer from './SummonerChampionsContainer'

export default class UserProfile extends Component{

    state = {
        summoner:  {} , 
        userLogin: false
    }

    componentDidUpdate(prevProps){
        console.log(prevProps.userLogin)
        console.log(this.props.userLogin)
        if(prevProps.userLogin !== this.props.userLogin){
            this.setState({userLogin: this.props.userLogin})
        }
    }

    handelLogin = () => {
        alert("Please Login To See Your Profile")
        this.props.history.push("/")
    }
    render(){
        console.log(this.props.userLogin)
        return(
            <div>
                <div className="summoner_main_container">

                    <div className="summoner_info_container">
                        {this.state.userLogin ? 
                            <div>
                                <ProfileContainer summoner = {this.state.summoner}/>
                                {!this.props.displayMatches ?
                                    <SummonerChampionsContainer 
                                    champions = {this.state.champiops}
                                    setChampionId = {this.props.setChampionId}
                                    history = {this.props.history}
                                    // showMatches={this.props.showMatches}
                                    // matches={this.props.matches}
                                    // displayMatches={this.props.displayMatches}
                                />
                                :
                                null 
                                // <MatchContainer 
                                // showMatches={this.props.showMatches} 
                                // matches={this.props.matches} 
                                // displayMatches={this.props.displayMatches}
                                // showChampions={this.props.showChampions}
                                // champions={this.props.champions}
                                // />
                                } 
                            </div>
                            :
                            this.handelLogin()
                        }
                    </div>
                </div>
            </div>
        )
    }
}
