import React , {Component} from 'react'

export default class Skins extends Component {

    constructor(){
        super()
        this.state = {
            skinIndex: 0
        }
    }

    handleClick = (num,max) => {
        let skinIndex = this.state.skinIndex + num > max ? 0 : this.state.skinIndex + num < 0 ? max : this.state.skinIndex + num
        this.setState({skinIndex})
    } 

    render(){
        let skins = this.props.skins
        return(
            <div id="carouselExampleFade" class="carousel slide carousel-fade" data-ride="carousel">
                <div class="carousel-inner">
                    <div class="carousel-item active">
                        <div class="carousel-caption d-none d-md-block">
                            <h1 className="champion_skin_title">{skins[this.state.skinIndex].name}</h1>
                        </div>
                        <img src={skins[this.state.skinIndex].image} class="d-block w-100" alt={skins[this.state.skinIndex].name}/>
                    </div>
                </div>
                <a class="carousel-control-prev" onClick = {() => this.handleClick(-1,skins.length - 1)}>
                    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                    {/* <span class="sr-only">Previous</span> */}
                </a>
                <a class="carousel-control-next" onClick = {() => this.handleClick(1, skins.length - 1)}>
                    <span class="carousel-control-next-icon" aria-hidden="true"></span>
                    <span class="sr-only">Next</span>
                </a>
            </div>
        )
    }
}
