import React from 'react'
import { Grid, Card, Image } from 'semantic-ui-react'

const ChampionCard = (props) => {
  return(
    // <Grid.Column>
      <Card
        // color='teal'
        >
          <Card.Content>
        <Image
          size='medium'
          // floated='left'
          src={props.champion.icon_image} />
        </Card.Content>
        <Card.Content>
        {`${props.champion.name}, ${props.champion.title}`}
        </Card.Content>
      </Card>
    // </Grid.Column>
  )
}

export default ChampionCard
