import React from 'react'
import SummonerChampionsCard from '../components/SummonerChampionsCard';
import MatchCard from '../components/MatchCard';


export default class SummonerChampionsContainer extends React.Component {

  // displayChampions = () => {
  //   let champions = this.props.champions 
  //   champions ? champions.map(champion => <SummonerChampionsCard champion={champion} setChampionId={this.props.setChampionId} history={this.props.history}/>)
  // }

  // displayMatches = () => {
  //   this.props.displayMatches === true ? this.props.matches.map(match => <MatchCard match={match}/>)
  // }

  render() {
    return(
      <div className = "match_container summoner_info_item">
        <button onClick={() => this.props.showMatches()}>{this.props.displayMatches === false ? "Show Match History" : "Show Champions"}</button>
        <div className= "top_champions">

            this.props.displayMatches === true ? this.props.displayMatches === true ? this.props.matches.map(match => <MatchCard match={match}/>) : this.props.champions.map(champion => <SummonerChampionsCard champion={champion} setChampionId={this.props.setChampionId} history={this.props.history}/>)




        </div>
      </div>
    )
  }
}


