import React from 'react'

export default class ChampionSearchBar extends React.Component {
  isFilterSelected = (value) => {
    return this.props.championsRoleFilters.includes(value)
    ? 'selectedFilterOption'
    : 'unselectedFilterOption'
  }

  isSortSelected = (value) => {
    return this.props.championsSortType === value
    ? 'selectedFilterOption'
    : 'unselectedFilterOption'
  }

  render() {
    return(
      <div id='champion-search-bar-container'>

      <div className='sort-filter-part'>
        <h3>Search for a Champion</h3>
        <input onChange={(e)=>this.props.searchChampions(e.target.value)} type='text' />
        </div>

        <div className='sort-filter-part'>

        <h3>Sort Champions</h3>
        <button onClick={()=>this.props.sortChampionsByType('alphabetically')} className={this.isSortSelected('alphabetically')}>Alphabetically</button>

        {/* might not be doable, depends on champion.json */}
        <button className={this.isSortSelected('byReleaseDate')}>By Release Date</button>
        </div>

        <h3>Filter Champions By Role</h3>
        <button
          onClick={(e)=>this.props.filterChampionsByRole(e.target.value)}
          className={this.isFilterSelected('Assassin')}
          value='Assassin'>Assassin</button><br/>
        <button
          onClick={(e)=>this.props.filterChampionsByRole(e.target.value)}
          className={this.isFilterSelected('Fighter')}
          value='Fighter'>Fighter</button><br/>
        <button
          onClick={(e)=>this.props.filterChampionsByRole(e.target.value)}
          className={this.isFilterSelected('Mage')}
          value='Mage'>Mage</button><br/>
        <button
          onClick={(e)=>this.props.filterChampionsByRole(e.target.value)}
          className={this.isFilterSelected('Marksman')}
          value='Marksman'>Marksman</button><br/>
        <button
          onClick={(e)=>this.props.filterChampionsByRole(e.target.value)}
          className={this.isFilterSelected('Support')}
          value='Support'>Support</button><br/>
        <button
          onClick={(e)=>this.props.filterChampionsByRole(e.target.value)}
          className={this.isFilterSelected('Tank')}
          value='Tank'>Tank</button><br/>

        <h3>Filter Champions By Difficulty</h3>
        <label><input type='radio' value='easy' />Easy</label><br/>
        <label><input type='radio' value='medium' />Medium</label><br/>
        <label><input type='radio' value='hard' />Hard</label><br/>

        <h3>Position (stretch goal)</h3>
        <label><input type='radio' value='top lane' />Top Lane</label><br/>
        <label><input type='radio' value='mid lane' />Mid Lane</label><br/>
        <label><input type='radio' value='ad carry' />AD Carry</label><br/>
        <label><input type='radio' value='support' />Support</label><br/>
        <label><input type='radio' value='jungle' />Jungle</label><br/>

      </div>
    )
  }
}
