import React , {Component} from 'react'
// import { Segment , Button , Input} from 'semantic-ui-react'
import {NavLink} from 'react-router-dom'

export default class Login extends Component {

    constructor(){
        super()
        this.state = {
            status: ""
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleLogin = () => {
        let obj = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username: this.state.username,
                password: this.state.password
            })
        }
        fetch("http://localhost:3000/user_login",obj)
        .then(res => res.json())
        .then(data => {
            if(data.errors){
                this.setState({status: data.errors})
            }else{
                this.setState({status: "Login"})
                localStorage.token = data.token
            }
        })
    }

    render() {
        return (
            <div className="login_container shadow-lg p-3 mb-5 bg-white rounded">
                {this.state.status}
                <div class="input-group flex-nowrap">
                    <label>Username</label><br/>
                    <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="addon-wrapping" onChange = {(e) => this.handleChange(e)}/>
                </div>
                {/* <label>Username</label>
                <input type="text" name="username" onChange = {(e) => this.handleChange(e)}/> */}
                <label>Password</label>
                <input type="text" name="password" onChange = {(e) => this.handleChange(e)}/>
                <br/>
                <button className="btn btn-outline-success" onClick={() => this.handleLogin()}>Login</button>
                <br/>
                <button className="btn btn-outline-success">
                    <NavLink to = "/signup">Sign Up</NavLink>
                </button>
            </div>
        )
    }
}
