import React from 'react'
import '../css/pure-css-progress-bars-master/assets/css/cssprogress.css'

export default class ChampionStatBars extends React.Component {

  render() {
    return(
<div>
      <p>ATTACK: {props.info.attack}</p>


      <p>DEFENSE: {props.info.defense}</p>
      <p>MAGIC: {props.info.magic}</p>
      <p>DIFFICULTY: {props.info.difficulty}</p>

      <div class="cssProgress">
      <div class="progress2">
        <div class="cssProgress-bar cssProgress-danger" data-percent={props.info.attack * 10} style={{transition: 'none 0s ease 0s', width: `${props.info.attack * 10}%`}}> <span class="cssProgress-label">{props.info.attack}</span></div>
      </div>
    </div>
</div>
    )
  }
}
