import React from 'react'
import ChampionCard from '../components/ChampionCard.js'
import { Grid, Image, Placeholder } from 'semantic-ui-react'

export default class ChampionsContainer extends React.Component {

  noChampions = () => {
    console.log('No Champions!')
    const placeholders = []
    for (let i = 0; i < 148; i++) {
      placeholders.push(
        <Placeholder.Image />
      )
    }
    return placeholders
  }

  test = () => {
    return <Image.Placeholder />
  }

  render() {
    return(
      <Grid columns={4} celled divided> {/* need to slice each row to get vertical dividers */}
          {this.props.champions.length > 0 ?
          this.props.champions.map(champion => <ChampionCard champion={champion} />) : this.test()}

        {/* <Grid.Row>
          {this.props.champions.length > 0 ?
          this.props.champions.slice(4,8).map(champion => <ChampionCard champion={champion} />) : console.log('No Champions!')}
        </Grid.Row> */}
      </Grid>
    )
  }
}
